import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Personaje } from '../interfaces/dbz.interface';

@Component({
  selector: 'app-agregar',
  templateUrl: './agregar.component.html',
})
export class AgregarComponent {

  //El input es para asociarselo en el personajes.html con las []

  // Creamos un nuevo objeto
  @Input() nuevo: Personaje = {
    nombre: '',
    poder: 0,
  }

  // agregar(event:any){
  //   // Evita que el boton de submit refresce todo el rato la pagina cuando le clicas lo podemos evitar importando en el module el FormsModule
  //   event.preventDefault();
  //   console.log(event);
  // }

  // cambiarNombre(event: any){
  //   console.log(event);
  // }

  @Output() onNuevoPers: EventEmitter<Personaje> = new EventEmitter();

  agregar(){

    // Si no escribes nada en el nombre validamos que no mande nada
    if (this.nuevo.nombre.trim().length === 0){
      return;
    }
    console.log(this.nuevo);

    // Mandamos el nuevo personaje para trabajar el evento, en el padre.html = main-page
    this.onNuevoPers.emit(this.nuevo);

    this.nuevo = {
      nombre: '',
      poder: 0
    }
  }

}
<h3>Agregar</h3>
<hr>

<!-- Enviamos el evento entero que genera el formulario -->
<!-- <form (submit)="agregar($event)"> -->
<form (ngSubmit)="agregar()">
    <!-- Las llaves [] son para enlazar una propiedad/variable de el lado del componente -->
    <input 
        type="text" 
        placeholder="Nombre"
        name="nombre"
        [(ngModel)]="nuevo.nombre"
    />
    <!-- Esta parte del código se puede cambiar por el ngModel -->
    <!-- 
        [value]="nuevo.nombre" 
        el input es para que a medida que escribes le manda al metodo de cambiar nombre pero con el ng model lo va actualizando en tiempo real de forma automática
        (input)="cambiarNombre($event)"
        -->
    <input 
        type="number" 
        placeholder="Poder"
        name="poder"
        [(ngModel)]="nuevo.poder"
    />
    <button type="submit">
        Agregar
    </button>
</form>
